sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function(strDate) {
		// Create GlideDateTime objects for the current date and the passed in date
		this.rightnow = new GlideDateTime();
		this.testdate = new GlideDateTime(strDate);
    },
	isDatePast: function(){
		// return true if the testdate is before rightnow, else return false
		return this.testdate.before(this.rightnow);
	},
	isDateToday: function(){
		var today = this.rightnow.getLocalDate();
		var inputDate = this.testdate.getLocalDate();
		// return true if the input date is today
		return today.compareTo(inputDate) == 0;
	},

    type: 'NeedItUtils'
};
